import React, { useState } from "react";
import {navigate, useNavigate } from "react-router-dom";
import axios from "axios";

const Add = () => {
  const [book, setBooks] = useState({
    title: "",
    desc: "",
    price: null,
    cover: "",
  });

  const navigate = useNavigate()
  const handleChange = (e) => {
    setBooks((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleClick = async( e) =>{
    e.preventDefault();
    try{
      await axios.post("http://localhost:3001/books",book)
navigate("/")
    }catch(err){
console.log(err)
    }
  }
  console.log(book);
  return (
    <div className="form">
      <h1>Add New Book</h1>
      <input
        type="text"
        placeholder="title"
        name="title"
        onChange={handleChange}
      />
      <input
        type="text"
        placeholder="desc"
        name="desc"
        onChange={handleChange}
      />
      <input
        type="number"
        placeholder="price"
        name="price"
        onChange={handleChange}
      />
      <input
        type="text"
        placeholder="cover"
        name="cover"
        onChange={handleChange}
      />

      <button onClick={handleClick}>Add </button>
    </div>
  );
};

export default Add;
